<?phpfunction GetMonthDay( $Year , $Month) {	echo $Year."-".$Month."-";    if($Month<12)$Month +=1;	else{		$Year+=1;		$Month=1;	}    $d=date ('d' , strtotime ( $Year .'-'. $Month .'-' . '01') - 1 );	echo $d."\r\n";	return $d;}function GetMultiMonthDay( $Year , $Month , $Much ) {	// $Year:起点年份 $Month:起点月份 $Much:计算多少个月	$j=0;	echo "1\r\n";	for( $i = 0;$i<$Much;$i++){		if($Month+$i>12){			$Yearx = $Year + intval(($Month+$i)/12);			$l = ($Month+$i)%12 ;		}else{			$Yearx = $Year;			$l = $Month+$i;		}		$j = $j + GetMonthDay($Yearx,$l);	}	return $j;}echo GetMultiMonthDay(date('Y'), 3 , 100) ."\n" ;?>