<?phpif(!defined('IN_DISCUZ')) {die('Access Denied');}if(!$discuz_uid){		showmessage('not_loggedin', NULL, array(), array('login' => 1));}$order_id = intval($_GET['order_id']);$subpage = intval($_GET['subpage']);$hid = intval($_GET['hid']);if ( !$subpage ) $subpage = 1;$this_p = $subpage - 1;$pos = $this_p * 20;$array = DB::fetch_all("SELECT order_id,type,paid,order_time,pay_time,json,need_pay,hid FROM  `host_order` WHERE `uid` = '{$discuz_uid}'".($hid?" AND `hid` = '$hid'":"")." ORDER BY order_time DESC LIMIT {$pos},20");$array_count = DB::fetch_all("SELECT count(order_id) as total FROM  `host_order` WHERE `uid` = '{$discuz_uid}' ".($hid?" AND `hid` = '$hid'":"")."");$nav_str = "";$total_page = ceil( $array_count[0]['total'] / 20 );for( $i = 1;$i <= $total_page; $i++ ){	if( $i == $subpage ){		$nav_str .= "<button class='btn' disabled>".$i."</button>";	}else{		$nav_str .= "<a class='btn' href='plugin.php?id=host:host&page=listorder&subpage={$i}".($hid?"&hid={$hid}":"")."'>".$i."</a>";							}}include template("host:listorder");?>